select * from bulk_service.public.cassandra_job_by_id
where id = 'c213135f-fd3d-41de-aa36-db8953c79834';

select * from bulk_service.public.cassandra_job_by_id
-- where operation_id = 'catalog_entity_to_ugp_linker'
order by created_time desc
limit 5;

select *
from bulk_service.public.cassandra_job_by_id j
left join bulk_service.public.cassandra_job_result r on r.job_id = j.id
where
    1 = 1
and j.category_id = 'retail_catalog'
-- and j.operation_id = 'retail_urpc_mpc_linker'
-- and j.created_time >= timestamp '2025-01-04'
-- and( j.created_by like '%2086147367%' or j.created_by like '%1785149365%')
order by j.created_time desc;
limit 100;

and j.created_by like '%Emily Williams%';

order by j.created_time
limit 200;
where id  = '3cfcda87-c302-4a64-8310-4a76000e1c8e'

select distinct error_message from bulk_service.public.cassandra_job_result;
         where error_message is not null
         limit 50;

select * from bulk_service.public.cassandra_job_summary limit 5;
where id = '3cfcda87-c302-4a64-8310-4a76000e1c8e';
