package com.doordash.airfleetservice.interceptor

import com.doordash.airfleetservice.service.DroneAuthService
import com.doordash.logging.KontextLogger
import com.google.inject.Inject
import com.google.inject.Singleton
import io.grpc.Context
import io.grpc.Contexts
import io.grpc.Metadata
import io.grpc.ServerCall
import io.grpc.ServerCallHandler
import io.grpc.ServerInterceptor
import io.grpc.Status

@Singleton
class DroneAuthInterceptor @Inject constructor(
    private val droneAuthService: DroneAuthService
) : ServerInterceptor {

    private val logger = KontextLogger.logger { }

    companion object {
        val DRONE_AUTH_HEADER: Metadata.Key<String> =
            Metadata.Key.of("drone-auth", Metadata.ASCII_STRING_MARSHALLER)
    }

    override fun <ReqT : Any?, RespT : Any?> interceptCall(
        call: ServerCall<ReqT, RespT>,
        headers: Metadata,
        next: ServerCallHandler<ReqT, RespT>
    ): ServerCall.Listener<ReqT> {
        try {
            val droneAuthToken = headers.get(DRONE_AUTH_HEADER)
            val grpcMethod = call.methodDescriptor.bareMethodName ?: "unknown"

            val authResult = droneAuthService.authenticate(droneAuthToken, grpcMethod)

            if (authResult.secretKeyUsed != null) {
                return Contexts.interceptCall(Context.current(), call, headers, next)
            } else {
                throw Status.UNAUTHENTICATED
                    .withDescription("Invalid drone authentication")
                    .asRuntimeException()
            }
        } catch (e: Exception) {
            logger.withValues(
                "exceptionType" to e.javaClass.simpleName,
                "message" to e.message
            ).error("[DroneAuthInterceptor] Error in drone authentication")
            throw Status.UNAUTHENTICATED
                .withDescription("Authentication failed")
                .asRuntimeException()
        }
    }
}

package com.doordash.airfleetservice.service

import com.doordash.airfleetservice.config.DroneAuthConfig
import com.doordash.logging.KontextLogger
import com.google.inject.Inject
import com.google.inject.Singleton

@Singleton
class DroneAuthService @Inject constructor(
    private val droneAuthConfig: DroneAuthConfig
) {
    private val logger = KontextLogger.logger { }

    data class DroneAuthResult(
        val secretKeyUsed: String? = null
    )

    fun authenticate(droneAuthToken: String?, methodName: String): DroneAuthResult {
        try {
            val secretMapping = droneAuthConfig.getDroneSecretMapping()

            val secretKeyUsed = secretMapping.entries
                .find { it.value == droneAuthToken }
                ?.key

            if (secretKeyUsed == null) {
                logger.withValues(
                    METHOD_KEY to methodName,
                    VALIDATION_RESULT_KEY to "INVALID_TOKEN"
                ).warn("[DroneAuthService] Invalid drone authentication token")
                return DroneAuthResult()
            }

            logger.withValues(
                METHOD_KEY to methodName,
                "secret_key" to secretKeyUsed,
                VALIDATION_RESULT_KEY to "SUCCESS"
            ).info("[DroneAuthService] Drone authenticated successfully")

            return DroneAuthResult(secretKeyUsed = secretKeyUsed)
        } catch (e: Exception) {
            logger.withValues(
                METHOD_KEY to methodName,
                "exceptionType" to e.javaClass.simpleName,
                "message" to e.message
            ).error("[DroneAuthService] Unexpected error during drone authentication", e)
            return DroneAuthResult()
        }
    }

    companion object {
        private const val METHOD_KEY = "method"
        private const val VALIDATION_RESULT_KEY = "validation_result"
    }
}

package com.doordash.payin.common.utils

import com.doordash.payin.common.json.gson
import com.doordash.payin.common.model.common.ValidationContextEvent
import com.doordash.payin.common.model.common.ValidationEventSource
import com.doordash.payin.common.model.common.ValidationEventType
import com.doordash.payin.common.producer.ValidationContextEventProducer
import com.google.common.hash.Hashing
import com.google.inject.Inject
import com.google.inject.Singleton
import io.opentelemetry.api.trace.Span
import io.opentelemetry.context.Context
import io.opentelemetry.context.ContextKey
import java.time.Instant

@Singleton
class ValidationFrameworkUtils @Inject constructor(
    private val producer: ValidationContextEventProducer,
) {
    fun <R> withValidation(
        functionName: String,
        eventType: ValidationEventType,
        args: Map<String, Any?>,
        block: () -> R,
    ): R {
        // Pre-process
        val traceId = Span.current().spanContext.takeIf { it.isValid }?.traceId
        val isValidationEnabled = Context.current().get(VALIDATION_CAPTURE_ENABLED_KEY) ?: false
        val shouldRecord = traceId != null && isValidationEnabled
        val timestamp = Instant.now()

        val result = runCatching(block)

        if (shouldRecord) {
            val requestJson = runCatching { gson.toJson(args) }.getOrElse { "{}" }
            val responseJson = result.fold(
                onSuccess = { runCatching { gson.toJson(it) }.getOrElse { "{}" } },
                onFailure = { runCatching { gson.toJson(it) }.getOrElse { "{}" } },
            )
            val event = ValidationContextEvent(
                traceId = traceId!!,
                functionName = functionName,
                timestamp = timestamp,
                eventType = eventType,
                sourceService = ValidationEventSource.PAYIN_PLATFORM,
                requestPayload = requestJson,
                responsePayload = responseJson,
            )
            producer.publish(event)
        }
        return result.getOrThrow()
    }

    suspend fun <R> withValidationSuspend(
        functionName: String,
        eventType: ValidationEventType,
        args: Map<String, Any?>,
        block: suspend () -> R,
    ): R {
        // Pre-process
        val traceId = Span.current().spanContext.takeIf { it.isValid }?.traceId
        val isValidationEnabled = Context.current().get(VALIDATION_CAPTURE_ENABLED_KEY) ?: false
        val shouldRecord = traceId != null && isValidationEnabled
        val timestamp = Instant.now()

        val result = runCatching { block() }

        if (shouldRecord) {
            val requestJson = runCatching { gson.toJson(args) }.getOrElse { "{}" }
            val responseJson = result.fold(
                onSuccess = { runCatching { gson.toJson(it) }.getOrElse { "{}" } },
                onFailure = { runCatching { gson.toJson(it) }.getOrElse { "{}" } },
            )
            val event = ValidationContextEvent(
                traceId = traceId!!,
                functionName = functionName,
                timestamp = timestamp,
                eventType = eventType,
                sourceService = ValidationEventSource.PAYIN_PLATFORM,
                requestPayload = requestJson,
                responsePayload = responseJson,
            )
            producer.publish(event)
        }
        return result.getOrThrow()
    }

    fun emitLabel(label: String) {
        val traceId = Span.current().spanContext.takeIf { it.isValid }?.traceId
        val isValidationEnabled = Context.current().get(VALIDATION_CAPTURE_ENABLED_KEY) ?: false
        val shouldRecord = traceId != null && isValidationEnabled

        if (shouldRecord) {
            val event = ValidationContextEvent(
                traceId = traceId!!,
                functionName = label,
                timestamp = Instant.now(),
                eventType = ValidationEventType.LABEL,
                sourceService = ValidationEventSource.PAYIN_PLATFORM,
                requestPayload = "",
                responsePayload = "",
            )
            producer.publish(event)
        }
    }

    companion object {
        private const val MASK_VALUE = 0x7FFFFFFF // Mask to ensure positive hash values
        val VALIDATION_CAPTURE_ENABLED_KEY: ContextKey<Boolean> = ContextKey.named("validation-capture-enabled")

        fun shouldSample(samplingRate: Double, traceId: String): Boolean {
            if (samplingRate <= 0.0) return false
            if (samplingRate >= 1.0) return true

            // Murmur3 provides better hash distribution than the hashCode function while having a similar performance.
            val hash = Hashing.murmur3_32_fixed().hashString(traceId, Charsets.UTF_8).asInt()
            val normalized = (hash and MASK_VALUE) / Int.MAX_VALUE.toDouble()

            return normalized < samplingRate
        }
    }
}
